mapper.select:
/* [memberMgmetDao.xml][select] 조건에 맞는 회원정보 가져오기 -닉네임 중복확인 */ SELECT COUNT(*) FROM (
SELECT MEMBER_ID, PASSWORD, NICK_NAME AS nickName, MEMBER_NAME, BIRTH, CDV, CV, CCV, MOBILE_NUMBER, MEMBER_INFO, REG_DATE, MANAGER_YN, WITHDRAW_YN, WITHDRAW_CODE, PERSONAL_INFO_YN, PERSONAL_INFO_REG_DATE FROM MEMBER_ WHERE 1=1
AND NICK_NAME = #{nickName}
) as memberTbl;
