mapper.select:
/* [noticeDao.xml][selectNoticeCount1] 공지사항 기본 selectList count  */ 
SELECT 
   COUNT(1) AS total
WHERE 1=1

AND TITLE LIKE CONCAT('%', #{titleKeyword}, '%')

FROM NOTICE;

/* [noticeDao.xml][selectNoticeCount2] 공지사항 기본 selectList */ SELECT NOTICE_NO AS noticeNo ,TITLE AS title ,DATE_FORMAT(REG_DATE, '%y-%m-%d) AS regDate FROM NOTICE WHERE 1=1
AND TITLE LIKE CONCAT('%', #{titleKeyword}, '%')
ORDER BY TOP_YN DESC, NOTICE_NO DESC LIMIT #{limit} OFFSET #{offset}
